(8, 'reg_validation', 'Проверка существования логина emaila', 2, 'none', 0, 0, '<script>\r\n\t\r\n\t$(document).ready(function(){\r\n        $("#password").pwdMeter();\r\n        $("#confirmpassword").pwdCompare();\r\n    });\r\n\t\r\n\tfunction existUsers(input) {\r\n\t\tif(input.id == "email") {\r\n\t\t   \r\n\t\t   $.post(\r\n\t\t\t  "/assets/snippets/exist_user.php",\r\n\t\t\t  {\r\n\t\t\t\temail: input.value\r\n\t\t\t  },\r\n\t\t\t  onAjaxSuccessEmail\r\n\t\t\t);\r\n\t\t}\r\n\t\telse if(input.id == "username") {\r\n\t\t\t\r\n\t\t\t$.post(\r\n\t\t\t  "/assets/snippets/exist_user.php",\r\n\t\t\t  {\r\n\t\t\t\tusername: input.value\r\n\t\t\t  },\r\n\t\t\t  onAjaxSuccessUser\r\n\t\t\t);\r\n\t\t}\r\n\t}\r\n\t\r\n\tfunction onAjaxSuccessEmail(data) {\r\n\t\t\r\n\t\t// Здесь мы получаем данные, отправленные сервером.\r\n\t\tif(data == ''yes'') {\r\n\t\t\tdocument.getElementById(''emailex'').style.visibility = "hidden";\r\n\t\t}\r\n\t\telse if(data == ''no''){\r\n\t\t\tdocument.getElementById(''emailex'').style.visibility = "visible";\r\n\t\t}\r\n\t}\r\n\t\r\n\tfunction onAjaxSuccessUser(data) {\r\n\t\t\r\n\t\t// Здесь мы получаем данные, отправленные сервером.\r\n\t\tif(data == ''yes'') {\r\n\t\t\tdocument.getElementById(''userex'').style.visibility = "hidden";\r\n\t\t}\r\n\t\telse if(data == ''no''){\r\n\t\t\tdocument.getElementById(''userex'').style.visibility = "visible";\r\n\t\t}\r\n\t}\r\n\t\r\n\tfunction validationPassword(input) {\r\n\t\t\r\n\t\tvar idMPsw;\r\n\t\tif(input.id == ''password'') {\r\n\t\t\tidMPsw = ''pwdMeter'';\r\n\t\t}\r\n\t\telse if(input.id == ''confirmpassword'') {\r\n\t\t\tidMPsw = ''pwdCompare'';\r\n\t\t}\r\n\t\t\r\n\t\tif(input.value == '''') {\r\n\t\t\tdocument.getElementById(idMPsw).style.visibility = "hidden";\r\n\t\t\t$(''#''+idMPsw).text('''');\r\n\t\t}\r\n\t\telse {\r\n\t\t\tdocument.getElementById(idMPsw).style.visibility = "visible";\r\n\t\t}\r\n\t\t\r\n\t}\r\n\t\r\n</script>', 0),
