(19, 'personal_area', 'личный кабинет', 0, 0, 0, '\r\nsession_start();\r\nif(isset($_SESSION[''webFullname''])){\r\n\techo "<ul id=''slide-out'' class=''side-nav''>\r\n\t\t\t<li><div class=''userView''>\r\n\t\t\t  <div class=''background''>\r\n\t\t\t\t<img src=''/assets/images/user_setting.jpg''>\r\n\t\t\t  </div>\r\n\t\t\t  <a href=''#!user''><img class=''circle'' src=''{$_SESSION[''webUserphoto'']}''></a>\r\n\t\t\t  <a href=''#!name''><span class=''white-text name''>{$_SESSION[''webFullname'']}</span></a>\r\n\t\t\t  <a href=''#!email''><span class=''white-text email''>{$_SESSION[''webEmail'']}</span></a>\r\n\t\t\t</div></li>\r\n\t\t\t<li><a href=''#''><i class=''material-icons light-blue-text''>visibility</i>Мои Надзиратели</a></li>\r\n\t\t\t<li><a href=''/new_wotch.html''><i class=''material-icons light-blue-text''>search</i>Установить Надзирателя</a></li>\r\n\t\t\t<li><a href=''{$url}?user=exit''><i class=''material-icons light-blue-text''>power_settings_new</i>Выход</a></li>\r\n\t\t  </ul>";\r\n}', 0, '{}', ' '),
(20, 'exit_user', 'выход', 0, 0, 0, '\r\nsession_start();\r\nif($_GET[''user''] === "exit"){\r\n\tunset($_SESSION[''webShortname'']);\r\n\tunset($_SESSION[''webFullname'']);\r\n\tunset($_SESSION[''webEmail'']);\r\n\tunset($_SESSION[''webValidated'']);\r\n\tunset($_SESSION[''webInternalKey'']);\r\n\tunset($_SESSION[''webValid'']);\r\n\tunset($_SESSION[''webUser'']);\r\n\tunset($_SESSION[''webFailedlogins'']);\r\n\tunset($_SESSION[''webLastlogin'']);\r\n\tunset($_SESSION[''webnrlogins'']);\r\n\tunset($_SESSION[''webUsrConfigSet'']);\r\n\tunset($_SESSION[''webUserGroupNames'']);\r\n\tunset($_SESSION[''webDocgroups'']);\r\n\tunset($_SESSION[''webUserphoto'']);\r\n\theader( ''Location: /'' );\r\n}', 0, '{}', ' '),
(21, 'authorization', 'Авторизация через форму', 0, 0, 0, '\r\nsession_start();\r\nif(isset($_POST[''email'']) && isset($_POST[''pass''])){\r\n\t$sql = "SELECT username, password, id\r\n\t\t\tFROM modx_web_users\r\n\t\t\tWHERE username=''".$_POST[''email'']."'' and password =''".md5($_POST[''pass''])."''";\r\n\t$result = $modx->db->query($sql);\r\n\t$members = $modx->db->makeArray($result);\r\n\tif($_POST[''email''] === $members[0][''username''] && md5($_POST[''pass'']) === $members[0][''password'']){\r\n\t\t$sql = "SELECT email, fullname, photo\r\n\t\t\tFROM modx_web_user_attributes\r\n\t\t\tWHERE id=''".$members[0][''id'']."''";\r\n\t\t$result = $modx->db->query($sql);\r\n\t\t$members = $modx->db->makeArray($result);\r\n\t\t$_SESSION[''webUserphoto''] = $members[0][''photo''];\r\n\t\t$_SESSION[''webFullname''] = $members[0][''fullname''];\r\n\t\t$_SESSION[''webEmail''] = $members[0][''email''];\r\n\t\t\r\n\t\theader( ''Location: /'' );\r\n\t}\r\n}', 0, '{}', ' '),
